$(document).ready(function() {
$(&lsquo;content_div&rsquo;).hide();</p>
<pre><code>$('#list_user').click(function(event){
    event.preventDefault();
    load_user();
});
</code></pre>
<p>});</p>
<p>function load_user(){
//load page
$(&quot;#content_div&rdquo;).load(&lsquo;your page url&rsquo;,function(){
//回调函数，在这里注册被加载页面需要的事件
$(&quot;.deleteItem&rdquo;).click(delItem);
$(&quot;.editItem&rdquo;).click(editItem);
});</p>
<pre><code>function delItem(){
    $.ajax({
        type:&quot;POST&quot;,
        url:&quot;your url&quot;,
        dataType:&quot;json&quot;,
        success:function(data){
        }
    });
}

function editItem(){
    $.ajax({
        type:&quot;POST&quot;,
        url:&quot;you url&quot;,
        dataType:&quot;json&quot;,
        success:function(data){
        }
    });
}
</code></pre>
