        $links .= ucwords($page['title']).'&lt;/a&gt;&lt;br /&gt;';

    endforeach;

    $data['links'] = $links;
}

for ($i=0; $i &lt; 10; $i++)
{
    $this-&gt;db-&gt;like('title', 'London');
    $query = $this-&gt;db-&gt;get('module_pages', 1);

    if ( $query-&gt;num_rows == 1 )
    {
        $row = $query-&gt;row_array();
        $row['body'] = str_replace('Getting', 'booya', $row['body']);

        $data['body'] = $row['body'];
    }
}

